import { Container, Content, Row } from "./styles";
import Input from "./components/Input";
import Button from "./components/Button";
import { useState } from "react";


const App = () => {
  const [currentNumber,setCurrentNumber] = useState('0');
  const [firstNumber,setFirstNumber] = useState('0');
  const [operation,setOperation] = useState('');
  const [visor,setVisor] = useState('0');
  const igual = Button.className.igual;


  const handleAddNumber = (num) => {
      setCurrentNumber(prev => `${prev === '0' ? '' : prev}${num}`);
      setVisor(prev => `${prev === '0' ? '' : prev}${num}`);
  }

  const handleOnClear = () => {
      setCurrentNumber('0');
      setFirstNumber('0');
      setOperation('');
      setVisor('0');
  }

  const handleSumNumbers = () => {
      if(firstNumber === '0'){
        setFirstNumber(String(currentNumber));
        setCurrentNumber('0');
        setOperation('+');
        setVisor('+');
      } else {
        const sum = Number(firstNumber) + Number(currentNumber);
        setCurrentNumber(String(sum));
        setVisor(String(sum));
        setOperation('');
      }

  }
  const handleEquals = () => {
    if(firstNumber !== '0'
                   && operation !== '' && currentNumber !== '0' ){
      switch(operation){
        case '+':
          handleSumNumbers();
          break;
        default:
          break;
      }
    }


  }


  return (
    <Container>
      <Content>
        <Input value={visor} />
        <Row>
          <Button label="7"  onClick={() => handleAddNumber('7')}/>
          <Button label="8"  onClick={() => handleAddNumber('8')}/>
          <Button label="9"  onClick={() => handleAddNumber('9')}/>
          <Button label="+"  onClick={() => handleSumNumbers()}/>
        </Row>
        <Row>
          <Button label="4" onClick={() => handleAddNumber('4')} />
          <Button label="5" onClick={() => handleAddNumber('5')}/>
          <Button label="6" onClick={() => handleAddNumber('6')}/>
          <Button label="-" />
        </Row>
        <Row>
          <Button label="1" onClick={() => handleAddNumber('1')}/>
          <Button label="2" onClick={() => handleAddNumber('2')}/>
          <Button label="3" onClick={() => handleAddNumber('3')}/>
          <Button label="*" />
        </Row>
        <Row>
          <Button label="0" onClick={() => handleAddNumber('0')} />
          <Button label="." />
          <Button label="Sqrt" />
          <Button label="/" onClick={handleOnClear}/>
        </Row>
        <Row>
          <Button label="Ln" onClick={() => handleAddNumber('0')} />
          <Button label="Pot" />
          <Button label="Limpa" onClick={handleOnClear}/>
          <Button label="=" className={igual} onClick={() => handleEquals()} />
        </Row>

      </Content>

    </Container>
  );
}

export default App;
